import styled from "styled-components";
import { BaseIcon } from "../../layout/assets/Icons";
import { Text } from "../common/styles";
import { summaryLogs } from "../../../mock/summary-mock";
import { useEffect ,useState} from "react";


const Container = styled.div`
  display: flex;
  justify-content: space-between;
  align-self: center;
`;

const DetailSquare = styled.div`
  position: relative;
  padding: 0 0;
  background-color: white;
  border-radius: 4px;
  box-shadow: 0 9px 28px 0 rgba(0, 0, 0, 0.03);
  background-color: #ffffff;
  display: flex;
  height: 100px;
  min-width: 264px;
`;

const Amount = styled.div`
  font-family: PingFangSC;
  font-size: 24px;
  line-height: 1.33;
  color: rgba(0, 0, 0, 0.85);
  margin-left: 26px;
`;


export const Content = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  width: 100%;
`;
const getIcon = (keyName)=>{
  return null;
}
const getPatchedLog=(summaryLog)=>{
  return summaryLog.map((log,i)=>{
    const icon = getIcon(log.key(log.amount));
    return {
      key: i,
      text: "Total",
      amount: 13000,
      icon: TotalSvg,
      color: "#2f9bff",
      name: "total",
    }
  })
   const summaryLogs = [
    {
      key: 1,
      text: "Total",
      amount: 13000,
      icon: TotalSvg,
      color: "#2f9bff",
      name: "total",
    },
    {
      key: 2,
      text: "Approved",
      amount: 126560,
      icon: ApprovedSvg,
      color: "#41d94e",
      name: "Approved",
    },
    {
      key: 3,
      text: "Declined",
      amount: 10143,
      icon: DeclinedSvg,
      color: "#e25e5e",
      name: "Declined",
    },
    {
      key: 4,
      text: "Approval Rate",
      amount: 78,
      icon: WaitingSvg,
      color: "#fc913f",
      name: "approvalRate",
    },
  ];

}
const Summary = ({summaryLog}) => {

  const [editedLog,setEditedLog] = useState([]);

  useEffect(()=>{
    if(summaryLog){
      const patchedLog = getPatchedLog(summaryLog);
    }

  },[])

  return (
    <Container>
      {summaryLogs.map((log) => {
        return (
          <DetailSquare key={log.key}>
            <BaseIcon src={log.icon} />{" "}
            <Content>
              <div>
                <Text color={log.color}>{log.text}</Text>
                <Amount>
                  {`${log.amount.toLocaleString()}${
                    log.name === "approvalRate" ? "%" : ""
                  }`}
                </Amount>
              </div>
            </Content>
          </DetailSquare>
        );
      })}
    </Container>
  );
};

export default Summary;
