import {
  LOAD_DATA,
  FILTER_DATA,
  PAGINATION_DATA,
  PAGINATION_LOAD,
  NEXT_PAGINATION,
  PREVIOUS_PAGINATION,
} from "./constants";
import { fetchTrs } from "../../api/transactions";
import { setAlert } from "./alert";

export const loadTransactions = () => async (dispatch) => {
  try {
    const fetchedData = await fetchTrs();
    dispatch({
      type: LOAD_DATA,
      payload: fetchedData.transactions,
    });
    dispatch(setAlert("transactions log loaded successfully", "success"));
  } catch (error) {
    debugger;
    if (!error.response) {
      dispatch(setAlert(error.message, "danger"));
    } else {
      const { errors } = error.response.data;
      if (errors) {
        errors.forEach((e) => {
          dispatch(setAlert(e.msg, "danger"));
        });
      }
    }
  }
};

export const filterData = (filterFields) => (dispatch) => {
  dispatch({
    type: FILTER_DATA,
    payload: filterFields,
  });
};

export const paginationData = (id) => (dispatch) => {
  dispatch({
    type: PAGINATION_DATA,
    payload: id,
  });
};

export const paginationLoad = (id) => (dispatch) => {
  dispatch({
    type: PAGINATION_LOAD,
    payload: id,
  });
};

export const nextPagination = (id) => (dispatch) => {
  dispatch({
    type: NEXT_PAGINATION,
    payload: id,
  });
};

export const previousPagination = (id) => (dispatch) => {
  dispatch({
    type: PREVIOUS_PAGINATION,
    payload: id,
  });
};
