import React from "react";
import styled from "styled-components";
import { getDeviceIcon, getPaymentType, getDotColor } from "./helper";
import { Text } from "../../../common/styles";
import { useDispatch, useSelector } from "react-redux";

export const Container = styled.div`
  background-color: white;
  padding: 32px 0 88px 0;
`;

const Tr = styled.tr`
  height: 54px;
  padding: 16px 0 0;
  background-color: #ffffff;
  border: 1px solid #e9e9e9;

  > td span {
    margin-left: 30px;
    font-family: PingFangSC;
    font-size: 14px;
    line-height: 1.57;
    color: rgba(0, 0, 0, 0.65);
  }
`;

const TableHeaders = styled.tr`
  height: 54px;
  padding: 16px 60px 16px 32px;
  background-color: #fafafa;
`;

const TableHeader = styled.th`
  font-family: PingFangSC;
  font-size: 14px;
  font-weight: 500;
  line-height: 1.57;
  color: rgba(0, 0, 0, 0.65);
  text-align: left;

  > span {
    margin: 0 32px 0 32px;
  }
`;
const Table = styled.table`
  margin-left: 32px;
  width: calc(100% - 64px);
  border-collapse: collapse;
`;

const tableTitles = [
  "Date/Time",
  "Name",
  "Device",
  "Payment",
  "BIN Number",
  "Amount",
  "Trans ID",
  "Decision",
];

const renderTableHeaders = () => {
  const header = Object.keys(tableTitles);
  return header.map((key, index) => {
    return (
      <TableHeader key={index}>
        <span>{tableTitles[key]}</span>
      </TableHeader>
    );
  });
};

const renderTableData = (data) => {
  return data.map((transaction, index) => {
    const {
      date,
      firstName,
      lastName,
      device,
      payment,
      binNumber,
      amount,
      transactionId,
      decision,
    } = transaction;
    const name = `${firstName} ${lastName}`;
    return (
      <Tr key={index}>
        <td>
          <span>{new Date(date).toLocaleString()}</span>
        </td>
        <td>
          <span>{name}</span>
        </td>
        <td>
          <span>{<img src={getDeviceIcon(device)} alt="" />}</span>
        </td>
        <td>
          <span>
            {
              <img
                style={{ width: "32px", height: "21px" }}
                src={getPaymentType(payment)}
                alt=""
              />
            }
          </span>
        </td>
        <td>
          <span>{binNumber}</span>
        </td>
        <td>
          <span>{`${amount}$`}</span>
        </td>
        <td>
          <span>{transactionId.slice(0, 10)}...</span>
        </td>
        <td>
          <span>
            <div>
              <Text color={getDotColor(decision)}>{decision}</Text>
            </div>
          </span>
        </td>
      </Tr>
    );
  });
};
const TransactionLog = () => {
  const { trs } = useSelector((state) => state);
  return (
    <Container>
      <Table>
        <tbody>
          <TableHeaders>{renderTableHeaders()}</TableHeaders>
          {trs.currentDisplayedData && renderTableData(trs.currentDisplayedData)}
        </tbody>
      </Table>
    </Container>
  );
};

export default TransactionLog;
