import styled from "styled-components";


export const Text = styled.div`
display: flex;
align-items: center;
font-family: PingFangSC;
font-size: 14px;
line-height: 1.57;
color: rgba(0, 0, 0, 0.45);
&::before {
  right: calc(100% + 8px);
  display: block;
  background-color: ${({ color }) => color};
  width: 6px;
  height: 6px;
  border-radius: 50%;
  content: "";
}
`;
