import React, { useState } from "react";
import styled from "styled-components";
import { ReactComponent as Logo } from "./assets/logo-nsure-white.svg";
import { ReactComponent as bilingIcon } from "./assets/billing.svg";
import { ReactComponent as transactionIcon } from "./assets/transaction.svg";

const SideBar = styled.div`
  width: 240px;
  background-color: rgba(1, 101, 255, 0.85);
  position: relative;
  width: ${({ collapsed }) => (collapsed ? "64px" : "240px")};
`;

export const Button = styled.button`
  position: absolute;
  left: 100%;
  transform: translateX(-50%);
  top: 20px;
  z-index: 2;
`;

const Header = styled.div`
  width: 240px;
  height: 64px;
  padding: 20px 53px 20px 70px;
  background-color: #0165ff;
`;
const SideBarItem = styled.div`
  height: 40px;
  background-color: #4f94fd;
`;

const ItemText = styled.span`
  display: ${({ collapsed }) => (collapsed ? "none" : "240px")};
  height: 20px;
  font-family: PingFangSC;
  font-size: 14px;
  font-weight: 500;
  line-height: 1.43;
  color: #ffffff;
`;

const NSureLogo = styled(Logo)`
  width: 117px;
  height: 24px;
  object-fit: contain;
`;

const IconContainer = styled.div`
  width: 24px;
  height: 24px;
  display: inline-block;
`;

const BilingIcon = styled(bilingIcon)`
  width: 16px;
  height: 16px;
`;

const TransactionIconn = styled(transactionIcon)`
  width: 16px;
  height: 16px;
`;

const Sidebar = () => {
  const [collapsed, setCollapsed] = useState(false);

  const toggleCollapsed = () => {
    setCollapsed(!collapsed);
  };

  return (
    <SideBar collapsed={collapsed}>
      <Header>
        <NSureLogo />
      </Header>
      <Button onClick={toggleCollapsed}>X</Button>
      <SideBarItem>
        <IconContainer>
          <TransactionIconn />
        </IconContainer>
        <ItemText collapsed={collapsed}>Transactions</ItemText>
      </SideBarItem>
      <SideBarItem>
        <IconContainer>
          <BilingIcon />
        </IconContainer>
        <ItemText collapsed={collapsed}>Billing </ItemText>
      </SideBarItem>
    </SideBar>
  );
};

export default Sidebar;
