import React from "react";
import styled from "styled-components";
import { DeclinedIcon, ApprovedIcon, WaitingIcon, TotalIcon } from "../../layout/assets/Icons";
import ApprovedSvg from '../../layout/assets/approved.svg'
import DeclinedSvg from '../../layout/assets/declined.svg'
import WaitingSvg from '../../layout/assets/waiting.svg'
import TotalSvg from '../../layout/assets/total.svg'

const Container = styled.div`
  display: flex;
  margin-top: 1rem;
  justify-content: space-between;
  align-self: center;
`;

const DetailSquare = styled.div`
  padding: 0 0;
  background-color: white;
  border-radius: 4px;
  box-shadow: 0 9px 28px 0 rgba(0, 0, 0, 0.03);
  background-color: #ffffff;
  display: flex;
  height:100px;
//   justify-content: center;

`;

const Text = styled.span`
  text-align: center;
  font-family: PingFangSC;
  font-size: 14px;
  font-weight: normal;
  font-stretch: normal;
  font-style: normal;
  line-height: 1.57;
  letter-spacing: normal;
  color: rgba(0, 0, 0, 0.45);
`;

const Dot = styled.span`
  background-color: ${({ color }) => color};
  width: 6px;
  height: 6px;
  border-radius: 50%;
  align-self: center;
  margin-right: 8px;
`;

const Amount = styled.span`
  font-family: PingFangSC;
  font-size: 24px;
  font-weight: normal;
  font-stretch: normal;
  font-style: normal;
  line-height: 1.33;
  letter-spacing: normal;
  color: rgba(0, 0, 0, 0.85);
`;

const logs = [
  {
    key: 1,
    text: "Total",
    amount: 13000,
    icon: TotalSvg,
    color: "#2f9bff",
    name: "total",
  },
  {
    key: 2,
    text: "Approved",
    amount: 126560,
    icon: ApprovedSvg,
    color: "#41d94e",
    name: "approved",
  },
  {
    key: 3,
    text: "Declined",
    amount: 10143,
    icon: DeclinedSvg,
    color: "#e25e5e",
    name: "declined",
  },
  {
    key: 4,
    text: "Approval Rate",
    amount: 78,
    icon: WaitingSvg,
    color: "#fc913f",
    name: "approvalRate",
  },
];

const getIcon = (log) => {
  const { name } = log;
  switch (name) {
    case "total":
      return <TotalIcon src = {log.icon} />;
    case "approved":
      return <ApprovedIcon src = {log.icon} />;
    case "declined":
      return <DeclinedIcon src = {log.icon} />;
    case "approvalRate":
      return <WaitingIcon  src = {log.icon}/>;
    default:
      return null;
  }
};

const Summary = () => {
  return (
    <Container>
      {logs.map((log) => {
        return (
          <DetailSquare key={log.key}>
            {getIcon(log)}
            <div
              style={{
                display: "flex",
                flexDirection: "column",
                width: "100px",
                alignItems: "center",
                alignSelf:'center',
                flexBasis:'66.6%',                

              }}
            >
              <div style={{ display: "flex" }}>
                <Dot color={log.color}></Dot>
                <Text>{log.text}</Text>
              </div>
              <Amount style={{ textAlign: "center" }}>
                {log.amount.toLocaleString()}
              </Amount>
            </div>
          </DetailSquare>
        );
      })}
    </Container>
  );
};

export default Summary;
