}

const initialState = {
  data: [],
  isLoading: true,
  currentDisplayedData: [],
};

const trsReducer = (state = initialState, action) => {
  const { type, payload } = action;
  switch (type) {
    case LOAD_DATA:
      return {
        ...state,
        data: payload,
        currentDisplayedData: payload,
        isLoading: false,
      };
    case FILTER_DATA: {
      const filteredData = [...state.currentDisplayedData];
      const newFilteredData = filterByFields(filteredData, payload);
      return null;
    }
    default:
      return state;
  }
};

export default trsReducer;
