import React, { useState } from "react";
import styled from "styled-components";
import { ReactComponent as Logo } from "./assets/logo-nsure-white.svg";
import billingIcon from "./assets/billing.svg";
import transactionIcon from "./assets/transaction.svg";
import minimize from "./assets/minimize.svg";
import maximize from "./assets/maximize.svg";

const SideBar = styled.div`
  background-color: rgba(1, 101, 255, 0.85);
  position: relative;
  width: ${({ collapsed }) => (collapsed ? "64px" : "240px")};
  display: flex;
  flex-direction: column;
`;

export const Button = styled.img`
  position: absolute;
  left: 100%;
  transform: translateX(-50%);
  top: 20px;
  width: 24px;
  height: 24px;
  z-index: 2;
`;

const Header = styled.div`
  height: 64px;
  background-color: #0165ff;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const SideBarItem = styled.div`
  display: flex;
  align-items: center;
  height: 40px;
  background-color: #4f94fd;
  justify-content: ${({ collapsed }) => (collapsed ? "center" : "center")};
`;

const ItemText = styled.span`
  display: ${({ collapsed }) => (collapsed ? "none" : "inline-block")};
  height: 20px;
  font-family: PingFangSC;
  font-size: 14px;
  font-weight: 500;
  line-height: 1.43;
  color: #ffffff;
  margin-left: 7px;
`;

const NSureLogo = styled(Logo)`
  width: 117px;
  height: 24px;
`;

const MenuIcon = styled.img`
  width: 24px;
  height: 24px;
`;

const Menu = styled.div`
  margin-top: 103px;
`;

const Sidebar = () => {
  const [collapsed, setCollapsed] = useState(false);

  const toggleCollapsed = () => {
    setCollapsed(!collapsed);
  };

  return (
    <SideBar collapsed={collapsed}>
      <Header>
        <NSureLogo />
      </Header>
      <Button src={collapsed ? maximize : minimize} onClick={toggleCollapsed} />
      <Menu>
        <SideBarItem>
          <MenuIcon collapsed={collapsed} src={transactionIcon} />
          <ItemText collapsed={collapsed}>Transactions</ItemText>
        </SideBarItem>
        <SideBarItem>
          <MenuIcon collapsed={collapsed} src={billingIcon} />
          <ItemText collapsed={collapsed}>Billing </ItemText>
        </SideBarItem>
      </Menu>
    </SideBar>
  );
};

export default Sidebar;
