import React,{ useState } from "react";
import styled from "styled-components";

const Container = styled.div`
  padding: 24px 32px 31px 32px;
  width: 100%;
  border-bottom: 1px solid #e9e9e9;
  background-color: white;
  margin-top: 20px;
`;

export const FormDesc = styled.text`
  margin: 8px 2px;
  width: 51px;
  height: 22px;
  font-family: PingFangSC;
  font-size: 14px;
  line-height: 1.57;
  color: rgba(0, 0, 0, 0.85);
  white-space: nowrap;
`;

export const Input = styled.input`
  height: 32px;
  padding: 5px 12px;
  border-radius: 4px;
  border: solid 1px rgba(0, 0, 0, 0.15);
  background-color: #ffffff;
  width: ${(props) => props.width};
  margin: ${(props) => props.margin};

  &::-webkit-input-placeholder {
    font-family: HelveticaNeue;
    font-size: 14px;
    line-height: 1.57;
    color: rgba(0, 0, 0, 0.25);
  }
`;

const SecondSearchRow = styled.form`
  display: flex;
`;

const Select = styled.select`
  width: 190px;
  height: 32px;
  padding: 5px 12px;
  border-radius: 4px;
  border: solid 1px rgba(0, 0, 0, 0.15);
  background-color: #ffffff;
  font-family: HelveticaNeue;
  font-size: 14px;
  line-height: 1.57;
  color: rgba(0, 0, 0, 0.25);
`;

const Wrapper = styled.div`
  display: flex;
  flex-direction: column;
  margin-left: ${(props) => (props.isFirst ? "0" : "15px")};
`;

export const SearchButton = styled.button`
  width: 77px;
  height: 32px;
  margin-top: 37px;
  margin-left: 15px;
  padding: 5px 14.5px 5px 16px;
  border-radius: 4px;
  background-color: #1890ff;
  font-family: PingFangSC;
  font-size: 14px;
  line-height: 1.57;
  color: #ffffff;
  border: none;
`;
export const ClearButton = styled.button`
  width: 65px;
  height: 32px;
  margin-top: 37px;
  margin-left: 6px;
  padding: 5px 14px 5px 16px;
  border-radius: 4px;
  border: solid 1px #d9d9d9;
  background-color: #ffffff;
  font-family: PingFangSC;
  font-size: 14px;
  line-height: 1.57;
  color: rgba(0, 0, 0, 0.65);
`;
const SearchFormFields = [
  {
    type: "text",
    name: "min",
    width: "88px",
    placeholder: "Min",
    label: "Amount",
  },
  {
    type: "text",
    name: "max",
    width: "88px",
    placeholder: "Max",
    label: "",
  },
  {
    type: "text",
    name: "binNumber",
    width: "88px",
    placeholder: "Bin Num",
    label: "BIN Number",
  },
  {
    type: "email",
    name: "email",
    width: "200px",
    placeholder: "Email",
    label: "Email",
  },
];

const Search = () => {
  const [formData, setFormData] = useState({
    transactionId: "",
    decision: "0",
    payment: "1",
    amount: {
      min: -1,
      max: MAX_VALUE,
    },

  });

  const handleChange = (e) => {};

  return (
    <Container>
      <Input
        type="text"
        name="transactionId"
        width="272px"
        onChange={handleChange}
        placeholder="Search Trans ID"
        //   value={formData[name]}
        style={{ marginBottom: "0.5rem" }}
      />
      <SecondSearchRow>
        <Wrapper isFirst>
          <FormDesc>Decision</FormDesc>
          <Select>
            <option value="all">All</option>
            <option value="approved">Approved</option>
            <option value="declined">Declined</option>
            <option value="reviewd">Reviewd</option>
            <option value="processing">Processing</option>
          </Select>
        </Wrapper>
        <Wrapper>
          <FormDesc>Payment</FormDesc>
          <Select>
            <option value="all">All</option>
            <option value="visa">Visa</option>
            <option value="amex">Amex</option>
            <option value="mastercard">Mastercard</option>
          </Select>
        </Wrapper>
        <Wrapper>
          <FormDesc>Amount</FormDesc>
          <div style={{ display: "flex" }}>
            <Input
              type="text"
              name="min"
              width="88px"
              //   onChange={(e) => onChange(e)}
              placeholder="Min"
              //   value={formData[name]}
            />
            <Input
              type="text"
              name="max"
              width="88px"
              //   onChange={(e) => onChange(e)}
              placeholder="Max"
              //   value={formData[name]}
            />
          </div>
        </Wrapper>
        <Wrapper>
          <FormDesc>BIN Number</FormDesc>
          <Input
            type="text"
            name="binNumber"
            width="88px"
            //   onChange={(e) => onChange(e)}
            placeholder="Bin Num"
            //   value={formData[name]}
          />
        </Wrapper>
        <Wrapper>
          <FormDesc>Email</FormDesc>
          <Input
            type="email"
            name="email"
            width="200px"
            //   onChange={(e) => onChange(e)}
            placeholder="Email"
            //   value={formData[name]}
          />
        </Wrapper>
        <SearchButton>Search</SearchButton>
        <ClearButton>Clear</ClearButton>

        {/* {SearchFormFields.map((formField, index) => {
          const { type, name, placeholder, label, width,margin } = formField;
          return (
            <Wrapper key={index}>
              <FormDesc>{label}</FormDesc>
              <Input
                type={type}
                name={name}
                width={width}
                margin={margin||0}
                //   onChange={(e) => onChange(e)}
                placeholder={placeholder}
                //   value={formData[name]}
              />
            </Wrapper>
        //   ); */}
        {/* })} */}
        {/* <Button onClick={(e) => onSubmit(e)}>
          Submit
        </Button> */}
      </SecondSearchRow>
    </Container>
  );
};

export default Search;
