import { LOAD_DATA, FILTER_DATA } from "../actions/constants";

const initialState = {
  data: [],
  isLoading: true,
  currentDisplayedData: [],
};

const trsReducer = (state = initialState, action) => {
  const { type, payload } = action;
  switch (type) {
    case LOAD_DATA:
      return {
        ...state,
        data: payload,
        currentDisplayedData: payload,
        isLoading: false,
      };
    case FILTER_DATA: {
      const filteredData = [...state.currentDisplayedData];
      return null;
    }
    default:
      return state;
  }
};

export default trsReducer;
