import { LOAD_DATA, FILTER_DATA } from "./constants";
import { fetchTrs } from "../../api/transactions";
import { setAlert } from "./alert";

export const loadTransactions = () => async (dispatch) => {
  try {
    const fetchedData = await fetchTrs();
    dispatch({
      type: LOAD_DATA,
      payload: fetchedData.transactions,
    });
    dispatch(setAlert("transactions log loaded successfully", "success"));
  } catch (error) {
    const { errors } = error.response.data;
    if (errors) {
      errors.forEach((e) => {
        dispatch(setAlert(e.msg, "danger"));
      });
    }
  }
};

export const filterData = (filterFields) => (dispatch) => {
  debugger
  dispatch({
    type: FILTER_DATA,
    payload: filterFields,
  });
};
