import { LOAD_DATA, FILTER_DATA } from "../actions/constants";

const filterByFields = (filteredData, payload) => {
  const { transactionId, binNumber } = payload;
  const newFilteredData = filteredData.filter((data) => {
    return data.transactionId.includes(transactionId);
  });

  return newFilteredData;
};

const initialState = {
  data: [],
  isLoading: true,
  currentDisplayedData: [],
};

const trsReducer = (state = initialState, action) => {
  const { type, payload } = action;
  switch (type) {
    case LOAD_DATA:
      return {
        ...state,
        data: payload,
        currentDisplayedData: payload,
        isLoading: false,
      };
    case FILTER_DATA: {
      const previousData = [...state.data];
      const newFilteredData = filterByFields(previousData, payload);
      return {
        ...state,
        currentDisplayedData: newFilteredData,
        isLoading: false,
      };
    }
    default:
      return state;
  }
};

export default trsReducer;
