import styled from "styled-components";

export const Text = styled.div`
  display: flex;
  align-items: center;
  position: relative;
  font-family: PingFangSC;
  font-size: 14px;
  line-height: 1.57;
  color: rgba(0, 0, 0, 0.45);
  margin-left: ${({ table }) => (table ? "15px" : "30px")};

  &::before {
    position: absolute;
    right: calc(100% + 8px);
    display: block;
    background-color: ${({ color }) => color};
    width: 6px;
    height: 6px;
    border-radius: 50%;
    content: "";
  }
`;
