import React from "react";
import styled from "styled-components";

import ApprovedSvg from "../../layout/assets/approved.svg";
import DeclinedSvg from "../../layout/assets/declined.svg";
import WaitingSvg from "../../layout/assets/waiting.svg";
import TotalSvg from "../../layout/assets/total.svg";
import { BaseIcon } from "../../layout/assets/Icons";
import { Text } from "../common/styles";

const Container = styled.div`
  display: flex;
  justify-content: space-between;
  align-self: center;
`;

const DetailSquare = styled.div`
  position: relative;
  padding: 0 0;
  background-color: white;
  border-radius: 4px;
  box-shadow: 0 9px 28px 0 rgba(0, 0, 0, 0.03);
  background-color: #ffffff;
  display: flex;
  height: 100px;
  min-width: 264px;
`;


const Amount = styled.div`
  font-family: PingFangSC;
  font-size: 24px;
  line-height: 1.33;
  color: rgba(0, 0, 0, 0.85);
`;

const Dot = styled.span`
  background-color: ${({ color }) => color};
  width: 6px;
  height: 6px;
  border-radius: 50%;
  align-self: center;
  margin-right: 8px;
`;

const logs = [
  {
    key: 1,
    text: "Total",
    amount: 13000,
    icon: TotalSvg,
    color: "#2f9bff",
    name: "total",
  },
  {
    key: 2,
    text: "Approved",
    amount: 126560,
    icon: ApprovedSvg,
    color: "#41d94e",
    name: "approved",
  },
  {
    key: 3,
    text: "Declined",
    amount: 10143,
    icon: DeclinedSvg,
    color: "#e25e5e",
    name: "declined",
  },
  {
    key: 4,
    text: "Approval Rate",
    amount: 78,
    icon: WaitingSvg,
    color: "#fc913f",
    name: "approvalRate",
  },
];

export const Content = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  width: 100%;
`;

const Summary = () => {
  return (
    <Container>
      {logs.map((log) => {
        return (
          <DetailSquare key={log.key}>
            <BaseIcon src={log.icon} />{" "}
            <Content>
              <div>
                <Text color={log.color}>{log.text}</Text>
                <Amount>{log.amount.toLocaleString()}</Amount>
              </div>
            </Content>
          </DetailSquare>
        );
      })}
    </Container>
  );
};

export default Summary;
