import React from "react";
import styled from "styled-components";
import {Declined,Approved,Waiting,Total} from '../../layout/common/Icons'

const Container = styled.div`
  display: flex;
  margin-top: 1rem;
  justify-content: space-between;
  width: 93%;
  align-self: center;
`;

const DetailSquare = styled.div`
  padding: 3rem 0;
  background-color: white;
  border-radius: 4px;
  box-shadow: 0 9px 28px 0 rgba(0, 0, 0, 0.03);
  background-color: #ffffff;
  flex-basis: 20%;
  display: flex;
  justify-content: center;
`;

const Text = styled.span`
  text-align: center;
  font-family: PingFangSC;
  font-size: 14px;
  font-weight: normal;
  font-stretch: normal;
  font-style: normal;
  line-height: 1.57;
  letter-spacing: normal;
  color: rgba(0, 0, 0, 0.45);
`;

const Dot = styled.span`
  background-color: ${({ color }) => color};
  width: 6px;
  height: 6px;
  border-radius: 50%;
  align-self: center;
  margin-right: 8px;
`;

const Amount=styled.span`
font-family: PingFangSC;
font-size: 24px;
font-weight: normal;
font-stretch: normal;
font-style: normal;
line-height: 1.33;
letter-spacing: normal;
color: rgba(0, 0, 0, 0.85);`;


const logs = [
  {
    key: 1,
    text: "Total",
    amount: 13000,
    icon: Total,
    color: "#2f9bff",
  },
  {
    key: 2,
    text: "Approved",
    amount: 126560,
    icon: Approved,
    color: "#41d94e",
  },
  {
    key: 3,
    text: "Declined",
    amount: 10143,
    icon: Declined,
    color: "#e25e5e",
  },
  {
    key: 4,
    text: "Approval Rate",
    amount: 78,
    icon: Waiting,
    color: "#fc913f",
  },
];

const Summary = () => {
  return (
    <Container>
      {logs.map((log) => {
        return (
          <DetailSquare key = {log.key}>
            <div style={{ display: "flex", flexDirection: "column" ,width:'100px',alignItems:'center'}}>
                <Component name={log.icon}/>
              <div style={{display:'flex'}}>
                <Dot color={log.color}></Dot>
                <Text>{log.text}</Text>
              </div>
              <Amount style={{textAlign:'center'}}>{log.amount.toLocaleString()}</Amount>
            </div>
          </DetailSquare>
        );
      })}
    </Container>
  );
};

export default Summary;
